import React from 'react'
import { useState } from 'react'
import Bookcabonline from '../Bookcabonline/Bookcabonline'
import './Bookcab.css'
import axios from "axios";


const Bookcab = () => {

const [User,setUser]=useState({
  Name:"",
  Email:"",
  Mobile:"",
  Pick:""
})

const handlechange=(e)=>{
  const {name,value}=e.target
  setUser({
    ...User,
    [name]:value
  })
}

const cabbook=()=>{
const {Name,Email,Mobile,Pick}=User
if(Name && Email && Mobile && Pick){
  alert ("Thanks for booking");
  axios.post("http://localhost:9002/bookcab",User)

}
else {
  alert ("Please Fill; Properly");

}
}

  return (

    <>
      {/* <Bookcabonline/> */}

      <div className="newform">
        <form action="" method="post">

          <input type="text" name='Name' value={User.Name} placeholder='Name' onChange={handlechange} />
          <input type="email" name='Email' value={User.Email} placeholder='Email'onChange={handlechange}  />
          <input type="text"  name='Mobile'value={User.Mobile} placeholder='Mobile'onChange={handlechange}  />
          <input type="text" name='Pick' value={User.Pick} placeholder='Pickup'onChange={handlechange}  />
          <button className='btna' onClick={cabbook} type='submit'>Submit</button>
        </form>
      </div>
    </>
  )
}

export default Bookcab
